def complement(dna_sequence):
    complement_map = {'A': 'T', 'T': 'A', 'G': 'C', 'C': 'G'}
    return ''.join(complement_map.get(base, base) for base in dna_sequence.upper())

# Example usage
seq = "ATCGATCG"
print(f"Original: {seq}")
print(f"Complement: {complement(seq)}")

def parse_fasta(fasta_content):
    sequences = {}
    current_header = None
    current_sequence = []

    for line in fasta_content.split('\n'):
        line = line.strip()
        if line.startswith('>'):
            if current_header:
                sequences[current_header] = ''.join(current_sequence)
            current_header = line[1:]
            current_sequence = []
        elif line:
            current_sequence.append(line)

    if current_header:
        sequences[current_header] = ''.join(current_sequence)

    return sequences

# Example usage
fasta_data = """>seq1 Description 1
ATCGATCGATCG
TAGCTAGC
>seq2 Description 2
GCGCGCGC"""

sequences = parse_fasta(fasta_data)
for header, seq in sequences.items():
    print(f"{header}: {seq}")
